     1‚Üíimport { createContext, useContext, useEffect, useState, ReactNode } from 'react';
     2‚Üíimport { User, Session } from '@supabase/supabase-js';
     3‚Üíimport { supabase } from '@/integrations/supabase/client';
     4‚Üíimport { useToast } from '@/hooks/use-toast';
     5‚Üíimport { useServices } from '@/services/ServiceProvider';
     6‚Üí
     7‚Üíinterface AuthContextType {
     8‚Üí  user: User | null;
     9‚Üí  session: Session | null;
    10‚Üí  signUp: (email: string, password: string, fullName?: string) => Promise<{ error: any }>;
    11‚Üí  signIn: (email: string, password: string) => Promise<{ error: any }>;
    12‚Üí  signInWithGoogle: () => Promise<{ error: any }>;
    13‚Üí  signOut: () => Promise<void>;
    14‚Üí  resetPassword: (email: string) => Promise<{ error: any }>;
    15‚Üí  loading: boolean;
    16‚Üí}
    17‚Üí
    18‚Üíconst AuthContext = createContext<AuthContextType | undefined>(undefined);
    19‚Üí
    20‚Üíexport function AuthProvider({ children }: { children: ReactNode }) {
    21‚Üí  const [user, setUser] = useState<User | null>(null);
    22‚Üí  const [session, setSession] = useState<Session | null>(null);
    23‚Üí  const [loading, setLoading] = useState(true);
    24‚Üí  const { toast } = useToast();
    25‚Üí  const { authService } = useServices();
    26‚Üí
    27‚Üí  useEffect(() => {
    28‚Üí    console.log('Setting up auth state listener');
    29‚Üí
    30‚Üí    // Set up auth state listener
    31‚Üí    const { data: { subscription } } = supabase.auth.onAuthStateChange(
    32‚Üí      (event, session) => {
    33‚Üí        console.log('Auth state changed:', event, session);
    34‚Üí        setSession(session);
    35‚Üí        setUser(session?.user ?? null);
    36‚Üí        setLoading(false);
    37‚Üí      }
    38‚Üí    );
    39‚Üí
    40‚Üí    // Check for existing session
    41‚Üí    supabase.auth.getSession().then(({ data: { session } }) => {
    42‚Üí      console.log('Initial session check:', session);
    43‚Üí      setSession(session);
    44‚Üí      setUser(session?.user ?? null);
    45‚Üí      setLoading(false);
    46‚Üí    });
    47‚Üí
    48‚Üí    return () => subscription.unsubscribe();
    49‚Üí  }, []);
    50‚Üí
    51‚Üí  const signUp = async (email: string, password: string, fullName?: string) => {
    52‚Üí    try {
    53‚Üí      const { user: newUser, error } = await authService.signUp(email, password, fullName);
    54‚Üí      
    55‚Üí      if (error) {
    56‚Üí        toast({
    57‚Üí          title: "Sign up failed",
    58‚Üí          description: error.message,
    59‚Üí          variant: "destructive",
    60‚Üí        });
    61‚Üí      } else {
    62‚Üí        const isConfirmed = newUser?.email_confirmed_at !== undefined;
    63‚Üí        
    64‚Üí        toast({
    65‚Üí          title: "Success!",
    66‚Üí          description: isConfirmed 
    67‚Üí            ? "Your account has been created. You can now sign in."
    68‚Üí            : "Please check your email to confirm your account.",
    69‚Üí        });
    70‚Üí      }
    71‚Üí      
    72‚Üí      return { error };
    73‚Üí    } catch (error) {
    74‚Üí      console.error('Sign up error:', error);
    75‚Üí      return { error };
    76‚Üí    }
    77‚Üí  };
    78‚Üí
    79‚Üí  const signIn = async (email: string, password: string) => {
    80‚Üí    try {
    81‚Üí      const { error } = await authService.signIn(email, password);
    82‚Üí      
    83‚Üí      if (error) {
    84‚Üí        toast({
    85‚Üí          title: "Sign in failed",
    86‚Üí          description: error.message,
    87‚Üí          variant: "destructive",
    88‚Üí        });
    89‚Üí      } else {
    90‚Üí        toast({
    91‚Üí          title: "Welcome back!",
    92‚Üí          description: "You have successfully signed in.",
    93‚Üí        });
    94‚Üí      }
    95‚Üí      
    96‚Üí      return { error };
    97‚Üí    } catch (error) {
    98‚Üí      console.error('Sign in error:', error);
    99‚Üí      return { error };
   100‚Üí    }
   101‚Üí  };
   102‚Üí
   103‚Üí  const signOut = async () => {
   104‚Üí    try {
   105‚Üí      await authService.signOut();
   106‚Üí      // Clear local state immediately
   107‚Üí      setUser(null);
   108‚Üí      setSession(null);
   109‚Üí      toast({
   110‚Üí        title: "Signed out",
   111‚Üí        description: "You have been successfully signed out.",
   112‚Üí      });
   113‚Üí      // Redirect to auth page
   114‚Üí      window.location.href = '/auth';
   115‚Üí    } catch (error) {
   116‚Üí      console.error('Sign out error:', error);
   117‚Üí    }
   118‚Üí  };
   119‚Üí
   120‚Üí  const signInWithGoogle = async () => {
   121‚Üí    try {
   122‚Üí      const { error } = await authService.signInWithOAuth('google');
   123‚Üí      
   124‚Üí      if (error) {
   125‚Üí        toast({
   126‚Üí          title: "Google sign in failed",
   127‚Üí          description: error.message,
   128‚Üí          variant: "destructive",
   129‚Üí        });
   130‚Üí      }
   131‚Üí      
   132‚Üí      return { error };
   133‚Üí    } catch (error) {
   134‚Üí      console.error('Google sign in error:', error);
   135‚Üí      return { error };
   136‚Üí    }
   137‚Üí  };
   138‚Üí
   139‚Üí  const resetPassword = async (email: string) => {
   140‚Üí    try {
   141‚Üí      const { error } = await authService.resetPassword(email);
   142‚Üí      return { error };
   143‚Üí    } catch (error) {
   144‚Üí      console.error('Reset password error:', error);
   145‚Üí      return { error: error as Error };
   146‚Üí    }
   147‚Üí  };
   148‚Üí
   149‚Üí  return (
   150‚Üí    <AuthContext.Provider value={{
   151‚Üí      user,
   152‚Üí      session,
   153‚Üí      signUp,
   154‚Üí      signIn,
   155‚Üí      signInWithGoogle,
   156‚Üí      signOut,
   157‚Üí      resetPassword,
   158‚Üí      loading
   159‚Üí    }}>
   160‚Üí      {children}
   161‚Üí    </AuthContext.Provider>
   162‚Üí  );
   163‚Üí}
   164‚Üí
   165‚Üíexport function useAuth() {
   166‚Üí  const context = useContext(AuthContext);
   167‚Üí  if (context === undefined) {
   168‚Üí    throw new Error('useAuth must be used within an AuthProvider');
   169‚Üí  }
   170‚Üí  return context;
   171‚Üí}

