     1‚Üí/**
     2‚Üí * useFeatureFlag Hook
     3‚Üí *
     4‚Üí * Simple feature flag evaluation using local configuration.
     5‚Üí * Since feature_flags table doesn't exist in the database,
     6‚Üí * this provides a local-only implementation for now.
     7‚Üí *
     8‚Üí * @example
     9‚Üí * ```tsx
    10‚Üí * const isAnalyticsEnabled = useFeatureFlag('brand_analytics', false);
    11‚Üí *
    12‚Üí * return isAnalyticsEnabled ? <Analytics /> : null;
    13‚Üí * ```
    14‚Üí */
    15‚Üíimport { useState, useEffect, useMemo } from 'react';
    16‚Üíimport { supabase } from '@/integrations/supabase/client';
    17‚Üí
    18‚Üí/**
    19‚Üí * Feature flag configuration (local-only for now)
    20‚Üí */
    21‚Üíinterface FeatureFlag {
    22‚Üí  name: string;
    23‚Üí  enabled: boolean;
    24‚Üí  targeting_rules?: {
    25‚Üí    userIds?: string[];
    26‚Üí    percentage?: number;
    27‚Üí    sessionPercentage?: number;
    28‚Üí  };
    29‚Üí}
    30‚Üí
    31‚Üí// Local feature flags configuration
    32‚Üíconst LOCAL_FEATURE_FLAGS: Record<string, FeatureFlag> = {
    33‚Üí  // Add feature flags here as needed
    34‚Üí  // 'brand_analytics': { name: 'brand_analytics', enabled: false },
    35‚Üí};
    36‚Üí
    37‚Üí/**
    38‚Üí * Get or create a session ID for anonymous users
    39‚Üí * Persists in sessionStorage for the duration of the browser session
    40‚Üí */
    41‚Üífunction getOrCreateSessionId(): string {
    42‚Üí  const SESSION_KEY = 'ff_session_id';
    43‚Üí
    44‚Üí  let sessionId = sessionStorage.getItem(SESSION_KEY);
    45‚Üí  if (!sessionId) {
    46‚Üí    sessionId = `anon_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`;
    47‚Üí    sessionStorage.setItem(SESSION_KEY, sessionId);
    48‚Üí  }
    49‚Üí
    50‚Üí  return sessionId;
    51‚Üí}
    52‚Üí
    53‚Üíexport interface UseFeatureFlagOptions {
    54‚Üí  /**
    55‚Üí   * Default value if flag doesn't exist
    56‚Üí   */
    57‚Üí  defaultValue?: boolean;
    58‚Üí
    59‚Üí  /**
    60‚Üí   * Optional user ID override (for admin testing)
    61‚Üí   */
    62‚Üí  userId?: string;
    63‚Üí
    64‚Üí  /**
    65‚Üí   * Optional session ID override (for testing)
    66‚Üí   */
    67‚Üí  sessionId?: string;
    68‚Üí}
    69‚Üí
    70‚Üí/**
    71‚Üí * Hook to evaluate a feature flag
    72‚Üí *
    73‚Üí * @param flagName - Unique feature flag name
    74‚Üí * @param defaultValue - Fallback value if flag doesn't exist (default: false)
    75‚Üí * @param options - Additional options
    76‚Üí * @returns boolean - Current flag evaluation result
    77‚Üí */
    78‚Üíexport function useFeatureFlag(
    79‚Üí  flagName: string,
    80‚Üí  defaultValue = false,
    81‚Üí  options: UseFeatureFlagOptions = {}
    82‚Üí): boolean {
    83‚Üí  const [flagValue, setFlagValue] = useState<boolean>(options.defaultValue ?? defaultValue);
    84‚Üí  const [userId, setUserId] = useState<string | undefined>(undefined);
    85‚Üí
    86‚Üí  // Get current user ID from Supabase auth
    87‚Üí  useEffect(() => {
    88‚Üí    const getUser = async (): Promise<void> => {
    89‚Üí      const { data: { user } } = await supabase.auth.getUser();
    90‚Üí      setUserId(user?.id);
    91‚Üí    };
    92‚Üí    getUser();
    93‚Üí  }, []);
    94‚Üí
    95‚Üí  // Determine user/session ID for evaluation
    96‚Üí  const evaluationContext = useMemo(() => {
    97‚Üí    return {
    98‚Üí      userId: options.userId || userId,
    99‚Üí      sessionId: options.sessionId || (!userId ? getOrCreateSessionId() : undefined),
   100‚Üí    };
   101‚Üí  }, [userId, options.userId, options.sessionId]);
   102‚Üí
   103‚Üí  // Evaluate flag from local configuration
   104‚Üí  useEffect(() => {
   105‚Üí    const flag = LOCAL_FEATURE_FLAGS[flagName];
   106‚Üí    
   107‚Üí    if (!flag) {
   108‚Üí      setFlagValue(options.defaultValue ?? defaultValue);
   109‚Üí      return;
   110‚Üí    }
   111‚Üí
   112‚Üí    const evaluated = evaluateFlag(flag, evaluationContext);
   113‚Üí    setFlagValue(evaluated);
   114‚Üí  }, [flagName, evaluationContext, defaultValue, options.defaultValue]);
   115‚Üí
   116‚Üí  return flagValue;
   117‚Üí}
   118‚Üí
   119‚Üí/**
   120‚Üí * Evaluate a feature flag based on targeting rules
   121‚Üí */
   122‚Üífunction evaluateFlag(
   123‚Üí  flag: FeatureFlag,
   124‚Üí  context: { userId?: string; sessionId?: string }
   125‚Üí): boolean {
   126‚Üí  // If flag is disabled globally, return false
   127‚Üí  if (!flag.enabled) {
   128‚Üí    return false;
   129‚Üí  }
   130‚Üí
   131‚Üí  // If no targeting rules, return global enabled state
   132‚Üí  if (!flag.targeting_rules || Object.keys(flag.targeting_rules).length === 0) {
   133‚Üí    return flag.enabled;
   134‚Üí  }
   135‚Üí
   136‚Üí  const rules = flag.targeting_rules;
   137‚Üí
   138‚Üí  // User ID targeting
   139‚Üí  if (rules.userIds && rules.userIds.length > 0 && context.userId) {
   140‚Üí    if (rules.userIds.includes(context.userId)) {
   141‚Üí      return true;
   142‚Üí    }
   143‚Üí  }
   144‚Üí
   145‚Üí  // Percentage rollout (user-based)
   146‚Üí  if (rules.percentage !== undefined && context.userId) {
   147‚Üí    const hash = simpleHash(context.userId);
   148‚Üí    const userPercentage = (hash % 100) + 1;
   149‚Üí    if (userPercentage <= rules.percentage) {
   150‚Üí      return true;
   151‚Üí    }
   152‚Üí  }
   153‚Üí
   154‚Üí  // Session-based percentage rollout
   155‚Üí  if (rules.sessionPercentage !== undefined && context.sessionId) {
   156‚Üí    const hash = simpleHash(context.sessionId);
   157‚Üí    const sessionPercentage = (hash % 100) + 1;
   158‚Üí    if (sessionPercentage <= rules.sessionPercentage) {
   159‚Üí      return true;
   160‚Üí    }
   161‚Üí  }
   162‚Üí
   163‚Üí  // Default to disabled if no rules matched
   164‚Üí  return false;
   165‚Üí}
   166‚Üí
   167‚Üí/**
   168‚Üí * Simple hash function for consistent percentage rollouts
   169‚Üí */
   170‚Üífunction simpleHash(str: string): number {
   171‚Üí  let hash = 0;
   172‚Üí  for (let i = 0; i < str.length; i++) {
   173‚Üí    const char = str.charCodeAt(i);
   174‚Üí    hash = ((hash << 5) - hash) + char;
   175‚Üí    hash = hash & hash; // Convert to 32bit integer
   176‚Üí  }
   177‚Üí  return Math.abs(hash);
   178‚Üí}
   179‚Üí
   180‚Üí/**
   181‚Üí * Hook to get raw feature flag data (for admin UI)
   182‚Üí *
   183‚Üí * @param flagName - Unique feature flag name
   184‚Üí * @returns Feature flag or null
   185‚Üí */
   186‚Üíexport function useFeatureFlagData(flagName: string): FeatureFlag | null {
   187‚Üí  const flag = LOCAL_FEATURE_FLAGS[flagName];
   188‚Üí  return flag || null;
   189‚Üí}
   190‚Üí
   191‚Üí/**
   192‚Üí * Hook to list all feature flags (for admin UI)
   193‚Üí *
   194‚Üí * @returns Array of all feature flags
   195‚Üí */
   196‚Üíexport function useAllFeatureFlags(): FeatureFlag[] {
   197‚Üí  return Object.values(LOCAL_FEATURE_FLAGS);
   198‚Üí}
