     1‚Üí/**
     2‚Üí * useDiagnostic Hook
     3‚Üí * React hook for diagnostic data operations
     4‚Üí */
     5‚Üí
     6‚Üíimport { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
     7‚Üíimport { useServices } from '@/services/ServiceProvider';
     8‚Üíimport { DiagnosticCreate } from '@/types/diagnostic';
     9‚Üíimport { useToast } from '@/hooks/use-toast';
    10‚Üí
    11‚Üíexport const useDiagnostic = () => {
    12‚Üí  const { diagnosticService } = useServices();
    13‚Üí  const queryClient = useQueryClient();
    14‚Üí  const { toast } = useToast();
    15‚Üí
    16‚Üí  // Query: Get latest diagnostic
    17‚Üí  const {
    18‚Üí    data: latestDiagnostic,
    19‚Üí    isLoading: isLoadingLatest,
    20‚Üí    error: latestError,
    21‚Üí  } = useQuery({
    22‚Üí    queryKey: ['diagnostic', 'latest'],
    23‚Üí    queryFn: () => diagnosticService.getLatestDiagnostic(),
    24‚Üí    retry: 1,
    25‚Üí  });
    26‚Üí
    27‚Üí  // Query: Get diagnostic history
    28‚Üí  const {
    29‚Üí    data: diagnosticHistory,
    30‚Üí    isLoading: isLoadingHistory,
    31‚Üí    error: historyError,
    32‚Üí  } = useQuery({
    33‚Üí    queryKey: ['diagnostic', 'history'],
    34‚Üí    queryFn: () => diagnosticService.getDiagnosticHistory(),
    35‚Üí    retry: 1,
    36‚Üí  });
    37‚Üí
    38‚Üí  // Mutation: Save diagnostic
    39‚Üí  const saveDiagnosticMutation = useMutation({
    40‚Üí    mutationFn: (data: DiagnosticCreate) => diagnosticService.saveDiagnostic(data),
    41‚Üí    onSuccess: () => {
    42‚Üí      queryClient.invalidateQueries({ queryKey: ['diagnostic'] });
    43‚Üí      toast({
    44‚Üí        title: 'Diagnostic Saved',
    45‚Üí        description: 'Your diagnostic results have been saved successfully.',
    46‚Üí      });
    47‚Üí    },
    48‚Üí    onError: (error: Error) => {
    49‚Üí      toast({
    50‚Üí        title: 'Error Saving Diagnostic',
    51‚Üí        description: error.message,
    52‚Üí        variant: 'destructive',
    53‚Üí      });
    54‚Üí    },
    55‚Üí  });
    56‚Üí
    57‚Üí  // Mutation: Sync from localStorage
    58‚Üí  const syncFromLocalStorageMutation = useMutation({
    59‚Üí    mutationFn: () => diagnosticService.syncFromLocalStorage(),
    60‚Üí    onSuccess: (data) => {
    61‚Üí      if (data) {
    62‚Üí        queryClient.invalidateQueries({ queryKey: ['diagnostic'] });
    63‚Üí        toast({
    64‚Üí          title: 'Diagnostic Synced',
    65‚Üí          description: 'Your diagnostic results have been synced to your account.',
    66‚Üí        });
    67‚Üí      }
    68‚Üí    },
    69‚Üí    onError: (error: Error) => {
    70‚Üí      console.error('Error syncing diagnostic:', error);
    71‚Üí    },
    72‚Üí  });
    73‚Üí
    74‚Üí  // Helper: Calculate scores
    75‚Üí  const calculateScores = (answers: Record<string, number>) => {
    76‚Üí    return diagnosticService.calculateScores(answers);
    77‚Üí  };
    78‚Üí
    79‚Üí  return {
    80‚Üí    // Data
    81‚Üí    latestDiagnostic,
    82‚Üí    diagnosticHistory,
    83‚Üí    
    84‚Üí    // Loading states
    85‚Üí    isLoadingLatest,
    86‚Üí    isLoadingHistory,
    87‚Üí    
    88‚Üí    // Errors
    89‚Üí    latestError,
    90‚Üí    historyError,
    91‚Üí    
    92‚Üí    // Mutations
    93‚Üí    saveDiagnostic: saveDiagnosticMutation.mutateAsync,
    94‚Üí    isSaving: saveDiagnosticMutation.isPending,
    95‚Üí
    96‚Üí    syncFromLocalStorage: syncFromLocalStorageMutation.mutateAsync,
    97‚Üí    isSyncing: syncFromLocalStorageMutation.isPending,
    98‚Üí    
    99‚Üí    // Helpers
   100‚Üí    calculateScores,
   101‚Üí  };
   102‚Üí};
   103‚Üí

