   127‚Üí/**
   128‚Üí * OnboardingTour Component
   129‚Üí *
   130‚Üí * Renders an interactive guided tour using React Joyride.
   131‚Üí * The tour highlights 4 key features: Diagnostic, IDEA Framework, Avatar Builder, and Brand Coach.
   132‚Üí *
   133‚Üí * @example
   134‚Üí * ```tsx
   135‚Üí * // Auto-start tour for new users
   136‚Üí * <OnboardingTour autoStart={true} />
   137‚Üí *
   138‚Üí * // Manual control with callbacks
   139‚Üí * <OnboardingTour
   140‚Üí *   onComplete={() => console.log('Tour completed')}
   141‚Üí *   onSkip={() => console.log('Tour skipped')}
   142‚Üí * />
   143‚Üí * ```
   144‚Üí */
   145‚Üíexport function OnboardingTour({
   146‚Üí  autoStart = false,
   147‚Üí  onComplete,
   148‚Üí  onSkip,
   149‚Üí}: OnboardingTourProps) {
