   155‚Üí    startTour,
   156‚Üí    completeTour,
   157‚Üí    skipTour,
   158‚Üí    setStepIndex,
   159‚Üí  } = useOnboardingTour();
   160‚Üí
   161‚Üí  // Track if tour start analytics have been fired to avoid duplicates
   162‚Üí  const hasTrackedStartRef = useRef(false);
   163‚Üí
   164‚Üí  // Auto-start tour for new users if autoStart is enabled
   165‚Üí  useEffect(() => {
   166‚Üí    if (autoStart && isReady && shouldShowTour()) {
   167‚Üí      // Small delay to ensure all target elements are mounted
   168‚Üí      const timeoutId = setTimeout(() => {
   169‚Üí        startTour();
   170‚Üí      }, 500);
   171‚Üí
   172‚Üí      return () => clearTimeout(timeoutId);
   173‚Üí    }
   174‚Üí  }, [autoStart, isReady, shouldShowTour, startTour]);
   175‚Üí
   176‚Üí  /**
   177‚Üí   * Handle Joyride callback events
   178‚Üí   * Manages tour state transitions and analytics tracking
   179‚Üí   */
   180‚Üí  const handleJoyrideCallback = useCallback(
   181‚Üí    (data: CallBackProps) => {
   182‚Üí      const { action, status, type, index, step } = data;
   183‚Üí
   184‚Üí      // Track tour started on first step (only once per tour run)
   185‚Üí      if (type === EVENTS.STEP_BEFORE && index === 0 && !hasTrackedStartRef.current) {
   186‚Üí        hasTrackedStartRef.current = true;
   187‚Üí        void trackTourStarted();
   188‚Üí      }
   189‚Üí
   190‚Üí      // Handle tour completion
   191‚Üí      if (status === STATUS.FINISHED) {
   192‚Üí        hasTrackedStartRef.current = false; // Reset for potential restart
   193‚Üí        completeTour(TOUR_STEPS.length);
   194‚Üí        void trackTourCompleted(TOUR_STEPS.length);
   195‚Üí        onComplete?.();
   196‚Üí        return;
   197‚Üí      }
   198‚Üí
   199‚Üí      // Handle tour skip
   200‚Üí      if (status === STATUS.SKIPPED) {
   201‚Üí        hasTrackedStartRef.current = false; // Reset for potential restart
   202‚Üí        skipTour(index);
   203‚Üí        void trackTourSkipped(index);
   204‚Üí        onSkip?.();
   205‚Üí        return;
   206‚Üí      }
   207‚Üí
   208‚Üí      // Handle step navigation and track step views
   209‚Üí      if (type === EVENTS.STEP_AFTER) {
   210‚Üí        // Track the step that was just viewed
   211‚Üí        const stepTarget = typeof step?.target === 'string' ? step.target : String(step?.target ?? '');
   212‚Üí        void trackStepViewed(index, stepTarget);
   213‚Üí
   214‚Üí        // Move to next step
   215‚Üí        if (action === 'next') {
   216‚Üí          setStepIndex(index + 1);
   217‚Üí        }
   218‚Üí        // Move to previous step
   219‚Üí        if (action === 'prev') {
   220‚Üí          setStepIndex(index - 1);
   221‚Üí        }
